mapper.select:
select sll.id, sll.userName, su.NAME as `name`, surn.roleName, sll.loginTime, sll.onlineTime, sll.callerIP, sll.log_sucesss_or_not from sys_login_log sll inner join sys_user su on sll.userName = su.USERNAME INNER JOIN ( SELECT sur.USERNAME, GROUP_CONCAT( sr.`NAME` SEPARATOR ',' ) roleName FROM sys_user_role sur INNER JOIN sys_role sr ON sur.ROLE_CODE = sr.`CODE` GROUP BY sur.USERNAME ) surn ON sll.userName = surn.USERNAME
and sll.loginTime >= #{start}
and sll.loginTime <= #{end}
and su.NAME like concat('%', #{nickName}, '%')
order by sll.loginTime desc
